mapper.select:
SELECT

FROM pet_pet


		AND name like '%${condition.name}%'


		AND weight = #{condition.weight}


		AND sex = #{condition.sex}


		AND age = #{condition.age}


		AND age > #{condition.startLife}


		AND age < #{condition.endLife}


		AND price = #{condition.price}


		AND price > #{condition.startPrice}


		AND price < #{condition.endPrice}


		AND quantity = #{condition.quantity}


		AND categoryCode = #{condition.categoryCode}



	ORDER BY ${orderBy} ${sortBy}


	limit ${offset}, ${rows}

SELECT
count(*)
FROM pet_pet


		AND name like '%${condition.name}%'


		AND weight = #{condition.weight}


		AND sex = #{condition.sex}


		AND age = #{condition.age}


		AND age > #{condition.startLife}


		AND age < #{condition.endLife}


		AND price = #{condition.price}


		AND price > #{condition.startPrice}


		AND price < #{condition.endPrice}


		AND quantity = #{condition.quantity}


		AND categoryCode = #{condition.categoryCode}
