mapper.insert:
        INSERT INTO order_item (
		    code
		    ,orderCode
		    ,petCode
		    ,quantity
		    ,totlalPrice
        ) VALUES (
		    	#{code}
		    	,#{orderCode}
		    	,#{petCode}
		    	,#{quantity}
		    	,#{totlalPrice}
        )

        	SELECT LAST_INSERT_ID() AS ID

mapper.update:
UPDATE order_item
code=#{code}, orderCode=#{orderCode}, petCode=#{petCode}, quantity=#{quantity}, totlalPrice=#{totlalPrice},
WHERE id = #{id}

UPDATE order_item


        code = #{update.code},


        orderCode = #{update.orderCode},


        petCode = #{update.petCode},


        quantity = #{update.quantity},


        totlalPrice = #{update.totlalPrice},





