        AND orderCode = #{condition.orderCode}


        AND petCode = #{condition.petCode}


        AND quantity = #{condition.quantity}


        AND totlalPrice = #{condition.totlalPrice}

mapper.delete:
DELETE FROM order_item
WHERE id in
#{id}

