mapper.select:
SELECT

FROM order_item

    AND id = #{id}


ORDER BY ${orderBy} ${sortBy}

SELECT

FROM order_item WHERE
${property} = #{value}

    ORDER BY ${orderBy} ${sortBy}

SELECT

FROM order_item



        and id in (#{condition.whereSql})



        ${condition.groupOp}   id  ${condition.id.op}  #{condition.id.data}


        ${condition.groupOp}   code  ${condition.code.op}  #{condition.code.data}


        ${condition.groupOp}   orderCode  ${condition.orderCode.op}  #{condition.orderCode.data}


        ${condition.groupOp}   petCode  ${condition.petCode.op}  #{condition.petCode.data}


        ${condition.groupOp}   quantity  ${condition.quantity.op}  #{condition.quantity.data}


        ${condition.groupOp}   totlalPrice  ${condition.totlalPrice.op}  #{condition.totlalPrice.data}






ORDER BY ${orderBy} ${sortBy}


    limit ${offset}, ${rows}

