limit 0,1

SELECT count(1) FROM order_item


       ${groupOp} id ${id.op}  #{id.data}


       ${groupOp} code ${code.op}  #{code.data}


       ${groupOp} orderCode ${orderCode.op}  #{orderCode.data}


       ${groupOp} petCode ${petCode.op}  #{petCode.data}


       ${groupOp} quantity ${quantity.op}  #{quantity.data}


       ${groupOp} totlalPrice ${totlalPrice.op}  #{totlalPrice.data}




