mapper.insert:
        INSERT INTO order_orders (
		    userCode
		    ,code
		    ,createTime
		    ,payTime
		    ,payType
		    ,sendTime
		    ,completeTime
		    ,deliveryType
		    ,deliveryTime
		    ,deliveryCode
		    ,totalPrice
		    ,supplyCode
		    ,description
		    ,status
        ) VALUES (
		    	#{userCode}
		    	,#{code}
		    	,#{createTime}
		    	,#{payTime}
		    	,#{payType}
		    	,#{sendTime}
		    	,#{completeTime}
		    	,#{deliveryType}
		    	,#{deliveryTime}
		    	,#{deliveryCode}
		    	,#{totalPrice}
		    	,#{supplyCode}
		    	,#{description}
		    	,#{status}
        )

        	SELECT LAST_INSERT_ID() AS ID

