mapper.insert:
        INSERT INTO pet_category (
		    code
		    ,name
		    ,animalCode
		    ,description
		    ,createTime
        ) VALUES (
		    	#{code}
		    	,#{name}
		    	,#{animalCode}
		    	,#{description}
		    	,#{createTime}
        )

        	SELECT LAST_INSERT_ID() AS ID

