mapper.insert:
        INSERT INTO pet_pet (
		    code
		    ,name
		    ,image
		    ,weight
		    ,sex
		    ,age
		    ,description
		    ,price
		    ,quantity
		    ,categoryCode
		    ,createTime
		    ,status
        ) VALUES (
		    	#{code}
		    	,#{name}
		    	,#{image}
		    	,#{weight}
		    	,#{sex}
		    	,#{age}
		    	,#{description}
		    	,#{price}
		    	,#{quantity}
		    	,#{categoryCode}
		    	,#{createTime}
		    	,#{status}
        )

        	SELECT LAST_INSERT_ID() AS ID

mapper.update:
UPDATE pet_pet
code=#{code}, name=#{name}, image=#{image}, weight=#{weight}, sex=#{sex}, age=#{age}, description=#{description}, price=#{price}, quantity=#{quantity}, categoryCode=#{categoryCode}, createTime=#{createTime}, status=#{status},
WHERE id = #{id}

UPDATE pet_pet


        code = #{update.code},


