mapper.insert:
        INSERT INTO pet_pet (
		    code
		    ,name
		    ,image
		    ,weight
		    ,life
		    ,price
		    ,description
		    ,createTime
		    ,categoryCode
        ) VALUES (
		    	#{code}
		    	,#{name}
		    	,#{image}
		    	,#{weight}
		    	,#{life}
		    	,#{price}
		    	,#{description}
		    	,#{createTime}
		    	,#{categoryCode}
        )

        	SELECT LAST_INSERT_ID() AS ID

mapper.update:
UPDATE pet_pet
code=#{code}, name=#{name}, image=#{image}, weight=#{weight}, life=#{life}, price=#{price}, description=#{description}, createTime=#{createTime}, categoryCode=#{categoryCode},
WHERE id = #{id}

UPDATE pet_pet


        code = #{update.code},


        name = #{update.name},


        image = #{update.image},


        weight = #{update.weight},


        life = #{update.life},


        price = #{update.price},


        description = #{update.description},


        createTime = #{update.createTime},


        categoryCode = #{update.categoryCode},





