mapper.insert:
        INSERT INTO user_user (
		    code
		    ,userName
		    ,password
		    ,phone
		    ,createTime
		    ,salt
		    ,locked
        ) VALUES (
		    	#{code}
		    	,#{userName}
		    	,#{password}
		    	,#{phone}
		    	,#{createTime}
		    	,#{salt}
		    	,#{locked}
        )

        	SELECT LAST_INSERT_ID() AS ID

mapper.update:
UPDATE user_user
code=#{code}, userName=#{userName}, password=#{password}, phone=#{phone}, createTime=#{createTime}, salt=#{salt}, locked=#{locked},
WHERE id = #{id}

UPDATE user_user


        code = #{update.code},


        userName = #{update.userName},


        password = #{update.password},


        phone = #{update.phone},


        createTime = #{update.createTime},


        salt = #{update.salt},


        locked = #{update.locked},





